import React,{Component} from 'react';
import './App.css';
import ReactDOM from 'react-dom'; 
import Navbar from './components/Navbar';
import CovidUpdate from './covidUpdate/CovidUpdate';
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      news : []
    }
  }


    componentDidMount()
    {
      fetch('https://cryptic-ravine-96718.herokuapp.com/')
     .then((response) => {
    return response.json();
      })
     .then((myJson) => {
       this.setState({
         news : myJson.news
       })
    });
    }
  render(){

    return(
    <div className= "App">
     <Navbar />
     <CovidUpdate />
      <div > 
       {this.state.news.map((item,index) => {
        return (
         <div>
          <h2 className = "heading"> <br>{item.title}</br> </h2> 
          <a className = "matter"
            href= {item.link}>
               Wanna read more          
            </a>
          <img  class= "image" src ={item.img} alt="logo" />
          </div>
       )})}
       </div>

    </div>
    )
  }
};
export default App;
