import React from 'react';
import logo from './logo.svg';
import './App.css';
import navbar from '../components';
import covidUpdate from './covidUpdate';
import ReactDOM from 'react-dom'; 
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      news : []
    }
  }


    componentDidMount()
    {
      fetch('https://cryptic-ravine-96718.herokuapp.com/')
     .then((response) => {
    return response.json();
      })
     .then((myJson) => {
       this.setState({
         news : myJson.news
       })
    });
    }
  render(){
    console.log(this.state)
    return(
    <div className = "heading">
      <navbar />
      <covidUpdate />
      <div style={{margin : "80px"}}> 
       {this.state.news.map((item,index) => {
        return (
         <div>
          <h1> {item.title} </h1> 
          <a class = "matter"
            href= {item.link}>
              Wanna read more
            </a>
          <img  class= "image" src ={item.img} alt="logo" />
          </div>
       )})}
       </div>

    </div>
    )
  }
};
export default App;
